/**
 * ã‚¤ãƒ³ãƒ—ãƒ¬ãƒ¼ã‚¹ç‰ˆã®ã‚¯ã‚¤ãƒƒã‚¯ã‚½ãƒ¼ãƒˆï¼ˆå…ƒã®é…åˆ—ã‚’å¤‰æ›´ï¼‰
 * 
 * @param arr ã‚½ãƒ¼ãƒˆå¯¾è±¡ã®æ•°å€¤é…åˆ—
 * @param low é–‹å§‹ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹
 * @param high çµ‚äº†ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹
 */
export const quickSortInPlace = (
  arr: number[], 
  low: number = 0, 
  high: number = arr.length - 1
): void => {
  if (low < high) {
    const pivotIndex = partition(arr, low, high);
    quickSortInPlace(arr, low, pivotIndex - 1);
    quickSortInPlace(arr, pivotIndex + 1, high);
  }
};

/**
 * é…åˆ—ã‚’åˆ†å‰²ã—ã¦ãƒ”ãƒœãƒƒãƒˆã®æœ€çµ‚ä½ç½®ã‚’è¿”ã™
 * 
 * @param arr å¯¾è±¡é…åˆ—
 * @param low é–‹å§‹ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹
 * @param high çµ‚äº†ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹
 * @returns ãƒ”ãƒœãƒƒãƒˆã®æœ€çµ‚ã‚¤ãƒ³ãƒ‡ãƒƒã‚¯ã‚¹
 */
const partition = (arr: number[], low: number, high: number): number => {
  const pivot = arr[high];
  let i = low - 1;

  for (let j = low; j < high; j++) {
    if (arr[j] <= pivot) {
      i++;
      [arr[i], arr[j]] = [arr[j], arr[i]]; // è¦ç´ ã‚’äº¤æ›
    }
  }

  [arr[i + 1], arr[high]] = [arr[high], arr[i + 1]]; // ãƒ”ãƒœãƒƒãƒˆã‚’æ­£ã—ã„ä½ç½®ã«é…ç½®
  return i + 1;
};

/**
 * ã‚¸ã‚§ãƒãƒªãƒƒã‚¯ç‰ˆã®ã‚¯ã‚¤ãƒƒã‚¯ã‚½ãƒ¼ãƒˆï¼ˆä»»æ„ã®å‹ã«å¯¾å¿œï¼‰
 * 
 * @param arr ã‚½ãƒ¼ãƒˆå¯¾è±¡ã®é…åˆ—
 * @param compareFn æ¯”è¼ƒé–¢æ•°
 * @returns ã‚½ãƒ¼ãƒˆæ¸ˆã¿ã®æ–°ã—ã„é…åˆ—
 */
export const quickSortGeneric = <T>(
  arr: T[], 
  compareFn: (a: T, b: T) => number
): T[] => {
  if (arr.length <= 1) {
    return [...arr];
  }

  const pivot = arr[0];
  const smaller = arr.slice(1).filter(element => compareFn(element, pivot) < 0);
  const equal = arr.filter(element => compareFn(element, pivot) === 0);
  const greater = arr.slice(1).filter(element => compareFn(element, pivot) > 0);

  return [
    ...quickSortGeneric(smaller, compareFn),
    ...equal,
    ...quickSortGeneric(greater, compareFn)
  ];
};